import pandas as pdimport numpy as npfrom survey.backend.src.strategies.matchmaking.implemented_strategies.naive_matchmaking_strategy import Strategyfrom survey.backend.src.strategies.preprocessing.vectorization import VectorizerDUMMY_RATINGS = pd.DataFrame(data=np.array([['459', '5618', '5.0', '1520233615'],                                            ['477', '5618', '4.0', '1201159360'],                                            ['298', '5618', '2.5', '1447598312'],                                            ['219', '1262', '3.5', '1194685902'],                                            ['483', '1262', '3.5', '1178293076'],                                            ['45', '1262', '5.0', '1020802351'],                                            ['606', '1262', '3.5', '1184619962'],                                            ['290', '1084', '5.0', '974938169'],                                            ['445', '6016', '2.0', '1454621781'],                                            ['455', '440', '3.0', '836436201']]),                             columns=['userId', 'movieId', 'rating', 'timestamp'])def test_naive_strategy_returns_existing_user():    naive_st = Strategy(DUMMY_RATINGS)    matched_user_id = str(naive_st.get_matched_offline_user())    offline_user_ids = DUMMY_RATINGS['userId'].unique()    assert matched_user_id in offline_user_ids, "the matched user by naive strategy does not exist in the offline users"# tests if the data frame from the `ratings.csv` is successfully transposed so that the columns are the itemsdef test_transpose_to_rating_matrix():    DUMMY_RATINGS2 = pd.DataFrame(data=np.array([[459, 5618, 5, 1520233615],                                                 [477, 5618, 5, 1201159360],                                                 [298, 5618, 1, 1447598312],                                                 [219, 5618, 1, 1194685902],                                                 [459, 1262, 1, 1178293076],                                                 [477, 1262, 5, 1020802351],                                                 [298, 1084, 1, 1184619962],                                                 [219, 1084, 5, 974938169]]),                                 columns=['userId', 'movieId', 'rating', 'timestamp'])    vectorizer = Vectorizer(DUMMY_RATINGS2)    rating_matrix = vectorizer.vectorize()    # user_id, item 5618, item 1262, item 1084, timestamp    TRANSPOSED_MATRIX = pd.DataFrame(data=np.array(pd.DataFrame(data=np.array([[459, 5, 1, None, 1520233615],                                                                               [477, 5, 5, None, 1201159360],                                                                               [476, 5, 4, None, 1201159360],                                                                               [298, 1, None, 1, 1447598312],                                                                               [219, 1, None, 5, 1194685902]]))))    assert rating_matrix == TRANSPOSED_MATRIX